import {Component} from 'react'
import Loader from 'react-loader-spinner'

import {Link} from 'react-router-dom'
import {CgProfile} from 'react-icons/cg'
import './index.css'

const user = localStorage.getItem("username")
location.reload(user)

class Profile extends Component {
  state = { blogData: {}, isLoading: true }
  
  componentDidMount() {
  
    this.getBlogItemData()
  }

  getBlogItemData =  () => {
    const use = user 
    this.setState({ blogData: use, isLoading: false })
   
  }

  renderBlogItemDetails = () => {
    
    const {blogData} = this.state
    const bD = blogData
    return (
      <>
      
      <div className="profile-section">
      <div className="profile-container">
        <CgProfile className="profile-icon" />
        <h1 className="profile-heading">{bD}</h1>

        <Link to="/">
          <button className="profile-home-page-button" type="button">
            Back To Home Page
          </button>
        </Link>
      </div>
    </div>
      </>
    )
  }

  renderLoader = () => (
    <div className="products-loader-container">
      <Loader type="Oval" color="#0b69ff" height="50" width="50" speed="25" />
    </div>
  )

  render() {
   
    const {isLoading} = this.state
    return isLoading ? this.renderLoader() : this.renderBlogItemDetails()
  }
}

export default Profile
