import {Component} from 'react'
import Loader from 'react-loader-spinner'
import Cookies from 'js-cookie'
import FiltersGroup from '../FiltersGroup'
import ProductCard from '../ProductCard'
import ProductsHeader from '../ProductsHeader'
import './index.css'



const categoryOptions = [
  {
    name: 'Clothing',
    categoryId: 'https://e-c-o-m-m-e-r-c-e.herokuapp.com/clothing',
  },
  {
    name: 'Electronics',
    categoryId: 'https://e-c-o-m-m-e-r-c-e.herokuapp.com/electronics',
  },
  {
    name: 'Appliances',
    categoryId: 'https://e-c-o-m-m-e-r-c-e.herokuapp.com/appliances',
  },
  {
    name: 'Grocery',
    categoryId: 'https://e-c-o-m-m-e-r-c-e.herokuapp.com/grocery',
  },
  {
    name: 'Toys',
    categoryId: 'https://e-c-o-m-m-e-r-c-e.herokuapp.com/toys',
  },
]

const sortbyOptions = [
  {
    optionId: 'https://e-c-o-m-m-e-r-c-e.herokuapp.com/high-price',
    displayText: 'Price (High-Low)',
  },
  {
    optionId: 'https://e-c-o-m-m-e-r-c-e.herokuapp.com/low-price',
    displayText: 'Price (Low-High)',
  },
]

const ratingsList = [
  {
    ratingId: '4',
    imageUrl:
      'https://assets.ccbp.in/frontend/react-js/rating-four-stars-img.png',
  },
  {
    ratingId: '3',
    imageUrl:
      'https://assets.ccbp.in/frontend/react-js/rating-three-stars-img.png',
  },
  {
    ratingId: '2',
    imageUrl:
      'https://assets.ccbp.in/frontend/react-js/rating-two-stars-img.png',
  },
  {
    ratingId: '1',
    imageUrl:
      'https://assets.ccbp.in/frontend/react-js/rating-one-star-img.png',
  },
]

const apiStatusConstants = {
  initial: 'INITIAL',
  success: 'SUCCESS',
  failure: 'FAILURE',
  inProgress: 'IN_PROGRESS',
}

class AllProductsSection extends Component {
  render(){
    categoryOptions.sort((a, b) => (a.id > b.id) ? -1 : 1)
    return (
        <div>
            
            <ul>
                {                                          
                    categoryOptions.map(item => {
                        return <div key={item.id}>{item.id},  {item.name}</div>
                    })
                }
            </ul>
            


        </div>
    )
              }

}
export default AllProductsSection
