import {Component} from 'react'
import axios from 'axios'
class Register extends Component {
  state = {
    name:'',
    username: '',
    password: '',
    gender:'',
    location:''
  }

  onChangeUsername = event => {
    this.setState({username: event.target.value})
  }
  onChangeName = event => {
    this.setState({name: event.target.value})
  }

  onChangePassword = event => {
    this.setState({password: event.target.value})
  }
  onChangeGender = event => {
    this.setState({gender: event.target.value})
  }
  onChangeLocation = event => {
    this.setState({location: event.target.value})
  }

  onSubmitSuccess = () => {
    const {history} = this.props

    history.replace('/login')
  }


  submitForm = async event => {
    event.preventDefault()
    const {name,username,password,gender,location} = this.state
   
  axios.post(`https://a-u-t-h-e-n-t-i-c-a-t-i-o-n.herokuapp.com/login`, { name,username,password,gender,location })
      .then(data => {
        if(data.status === 200 ){
          this.onSubmitSuccess()
        }
       
      })
  }
  renderNameField = () => {
    const {name} = this.state
    return (
      <>
        <label  htmlFor="name">
          NAME
        </label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={this.onChangeName}
        />
      </>
    )
  }
  renderUsernameField = () => {
    const {username} = this.state
    return (
      <>
        <label  htmlFor="username">
          USERNAME
        </label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={this.onChangeUsername}
        />
      </>
    )
  }
  renderPasswordField = () => {
    const {password} = this.state
    return (
      <>
        <label  htmlFor="password">
          PASSWORD
        </label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={this.onChangePassword}
        />
      </>
    )
  }
  renderGenderField = () => {
    const {gender} = this.state
    return (
      <>
        <label  htmlFor="gender">
          GENDER
        </label>
        <input
          type="text"
          id="gender"
          value={gender}
          onChange={this.onChangeGender}
        />
      </>
    )
  }
  renderLocationField = () => {
    const {location} = this.state
    return (
      <>
        <label  htmlFor="location">
          LOCATION
        </label>
        <input
          type="text"
          id="location"
          value={location}
          onChange={this.onChangeLocation}
        />
      </>
    )
  }

  render() {
    return (
      <div >
       
        <form className="form-container" onSubmit={this.submitForm}>
         
          <div >{this.renderNameField()}</div>
          <div >{this.renderUsernameField()}</div>
          <div>{this.renderPasswordField()}</div>
          <div>{this.renderGenderField()}</div>
          <div >{this.renderLocationField()}</div>
          <button type="submit" >
            Create an account
          </button>
        </form>
      </div>
    )
  }
}

export default Register
